mapper.select:
SELECT AUTH
FROM B_USER
WHERE USERID=#{userId}

SELECT USERID
FROM B_USER
WHERE USERID = #{userId}

SELECT EMAIL
FROM B_USER
WHERE EMAIL = #{email}

SELECT TEL
FROM B_USER
WHERE TEL = #{tel}

mapper.update:
UPDATE B_USER
SET USERPW = #{userPw},
	USERNAME = #{userName},
	EMAIL = #{email},
	TEL = #{tel} 
WHERE USERID = #{userId}
