mapper.select:
SELECT COUNT(BNO)
FROM BOARD
WHERE CNO = #{cNo}

mapper.update:
UPDATE B_REPLY
SET RCONTENT = #{rContent}, UDATE = SYSDATE
WHERE RNO = #{rNo}

mapper.select:
SELECT COUNT(RNO)
FROM B_REPLY
WHERE BNO = #{bNo}

SELECT ENABLED
FROM B_LIKE
WHERE BNO = #{bNo} AND USERID = #{userId}

mapper.update:
UPDATE B_LIKE
SET ENABLED = #{enabled}
WHERE BNO = #{bNo} AND USERID = #{userId}
